from collections import deque

class TreeNode:
    def __init__(self, value):
        self.value = value
        self.children = []

def highest_common_ancestor(root, node1, node2):
    if root == None or node1 == None or node2 == None:
        return None

    if root == node1 or root == node2:
        return root

    if root in [node1, node2]:
        return root

    stack = deque([(root, node1, node2)])
    seen = set([root])

    while stack:
        current, node1, node2 = stack.popleft()

        if current in seen:
            continue

        seen.add(current)

