    public static int partition(List<Integer> list, int low, int high, int pivotIndex) {
        if (low == high) {
            if (low + 1 == pivotIndex) {
                return low;
            } else {
                return -1; // Partition failed
            }
        }

        int pivotValue = list.get(pivotIndex);
        int i = low - 1;

